#The four adjacent digits in the 1000-digit number that have the greatest product are 9 × 9 × 8 × 9 = 5832.
#Find the thirteen adjacent digits in the 1000-digit number that have the greatest product. What is the value of this product?

digit_array_string = '
0588611646710940507754100225698315520005593572972571636269561882670428252483600823257530420752963450'.split(//)
digits = digit_array_string.map {|x| x.to_i}
largest_multiple = 0
x=0
while x+14 != digits.length      #using x as the location in the array I need to stop somewhere. Three lines dedicated to this, prime for refactor.
  product_of_13 = 1
  (0..12).each do |y|
    product_of_13 *= digits[x+y]
  end
  if product_of_13 > largest_multiple
    largest_multiple = product_of_13
  end
  x+=1
end
puts largest_multiple
